// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

contract NFTStakingVault is Ownable, IERC721Receiver, ReentrancyGuard{
    AggregatorV3Interface internal btc_usd_price_feed;

    uint256 public totalNftsStaked;
    uint256 private constant stakePeriod = 30 days;    
    uint256 public rewardPerMonth;
    uint256 public rewardPerYear;

    IERC20 token;
    IERC721 nft;

    struct Stake {
        address owner;
        uint256 stakeTime;
    }

    mapping(uint256 => Stake) private vault;

    mapping(uint256 => uint256) private claimedAt;

    //--------------------------------------------------------------------
    // ERRORS

    error NFTStakingVault__ItemAlreadyStaked();
    error NFTStakingVault__NotNFTOwner();
    error NFTStakingVault__InvalidUser();
    error NFTStakingVault__InvalidAmount();
    error NFTStakingVault__MaxClaimAmountExceeds();
    error NFTStakingVault__RewardClaimedForThisMonth();
    error NFTStakingVault__CannotbeUnstakedAtThisTime();

    //--------------------------------------------------------------------
    // CONSTRUCTOR
    // 0xfdFD9C85aD200c506Cf9e21F1FD8dd01932FBB23
    // 0xA39434A63A52E749F02807ae27335515BA4b07F7

    constructor(address _btcAggregator,address _nftAddress, address _tokenAddress) {
        btc_usd_price_feed = AggregatorV3Interface(_btcAggregator);

        nft = IERC721(_nftAddress);
        token = IERC20(_tokenAddress);
    }

    //--------------------------------------------------------------------
    // FUNCTIONS

    function stakeNft(uint256[] calldata tokenIds) external {
        address user = _msgSender();
        if(user == address(0)) {
            revert NFTStakingVault__InvalidUser();
        }

        uint256 tokenId;
        uint256 stakedCount;

        uint256 len = tokenIds.length;

        for (uint256 i; i < len; i++) {

            tokenId = tokenIds[i];

            if (vault[tokenId].owner != address(0)) {
                revert NFTStakingVault__ItemAlreadyStaked();
            }

            if (nft.ownerOf(tokenId) != _msgSender()) {
                revert NFTStakingVault__NotNFTOwner();
            }

            nft.safeTransferFrom(user, address(this), tokenId);

            vault[tokenId] = Stake(user, block.timestamp);

            emit ItemStaked(tokenId, user, block.timestamp);

            stakedCount++;
        }

        totalNftsStaked += stakedCount;
    }

    function unstakeNft(uint256[] calldata tokenIds) external {
        address user = _msgSender();
        if(user == address(0)) {
            revert NFTStakingVault__InvalidUser();
        }

        uint256 tokenId;
        uint256 unstakedCount;

        uint256 len = tokenIds.length;

        uint256 totalReward;

        for (uint256 i; i < len; i++) {
            tokenId = tokenIds[i];

            if(vault[tokenId].owner != user) {
                revert NFTStakingVault__NotNFTOwner();
            }

            if(stakePeriod > block.timestamp - vault[tokenId].stakeTime) {
                revert NFTStakingVault__CannotbeUnstakedAtThisTime();
            }

            delete vault[tokenId];

            totalReward += rewardPerYear;

            nft.safeTransferFrom(address(this), user, tokenId);

            emit ItemUnstaked(tokenId, user, block.timestamp);

            unstakedCount++;
        }

        if (totalReward != 0) {
            token.transfer(user, totalReward);
        }

        totalNftsStaked -= unstakedCount;
    }

    function claimReward(uint256[] calldata tokenIds) external {
        address user = _msgSender();
        if(user == address(0) || user == address(this)) {
            revert NFTStakingVault__InvalidUser();
        }

        uint256 tokenId;
        uint256 calculatedReward;
        (uint256 btcReward,) = viewBtcReward();

        uint256 len = tokenIds.length;

        calculatedReward = btcReward * tokenIds.length;

        if(calculatedReward > 10**7) {
            revert NFTStakingVault__MaxClaimAmountExceeds();
        }

        for (uint256 i; i < len; i++ ) {

            tokenId = tokenIds[i];

            if (nft.ownerOf(tokenId) != user) {
                revert NFTStakingVault__NotNFTOwner();
            }

            if(block.timestamp - claimedAt[tokenId] < 30 days) {
                revert NFTStakingVault__RewardClaimedForThisMonth();
            }

            claimedAt[tokenId] = block.timestamp;
        }

        if (calculatedReward != 0) {
            token.transfer(user, calculatedReward);
        }

        emit Claimed(user, calculatedReward);
    }

    function withdrawWBTC() external onlyOwner() {
        uint256 amount = token.balanceOf(address(this));

        token.transfer(_msgSender(), amount);
    }

    function setBtcReward(uint256 _rewardPerMonth) external onlyOwner {
        if(_rewardPerMonth == 0) {
            revert NFTStakingVault__InvalidAmount();
        }

        rewardPerMonth = _rewardPerMonth;
    }

    //--------------------------------------------------------------------
    // VIEW FUNCTIONS

    /**
     * Returns the latest price
     */
    function getBtcUsd() public view returns (int) {
        (,int price,,,) = btc_usd_price_feed.latestRoundData();
        return price;
    }

    function viewBtcReward() public view returns (uint256,uint256) {
        (,int price,,,) = btc_usd_price_feed.latestRoundData();
        uint256 temp = 10**8;
        uint256 i;
        uint256 res = (uint256(price) / 3);
        for(i = 0; i < 8; i++){
            if((temp / res) != 0) {
                i = i-1;
                break;
            }
            else {
                temp = temp * 10;
            }
        }

        return (temp*10**(8-(i+1))/res,i);
    }

    function balanceOf(address user) public view returns (uint256 nftStakedbalance) {
        uint256 supply = nft.totalSupply();
        unchecked {
            for (uint256 i; i <= supply; ++i) {
                if (vault[i].owner == user) {
                    nftStakedbalance += 1;
                }
            }
        }
    }

    function tokensOfOwner(address user) public view returns (uint256[] memory tokens) {
        uint256 balance = balanceOf(user);
        uint256 supply = nft.totalSupply();
        tokens = new uint256[](balance);

        uint256 counter;

        if (balance == 0) {
            return tokens;
        }

        unchecked {
            for (uint256 i; i <= supply; ++i) {
                if (vault[i].owner == user) {
                    tokens[counter] = i;
                    counter++;
                }
                if (counter == balance) {
                    return tokens;
                }
            }
        }
    }

    function onERC721Received(address,address,uint256,bytes calldata) external pure override returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

    //--------------------------------------------------------------------
    // EVENTS

    event ItemStaked(uint256 tokenId, address owner, uint256 timestamp);
    event ItemUnstaked(uint256 tokenId, address owner, uint256 timestamp);
    event Claimed(address owner, uint256 reward);
}

